fileSystem = require('fs');
var _diretorioBase = './view/';

var _abrirHtml = function (nome, res){
	fileSystem.readFile(_diretorioBase+nome, function(err, data){
		

		res.writeHead(200, {'Content-type':'text/html'});
		res.write(data);
		res.end();


	});
}

module.exports = {

	diretorioBase: _diretorioBase,
	abrirHtml: _abrirHtml

}