var arquivo = require('rotas/arquivo.js');
urls = require('url');

var _router = function(res, req)
{
	url = urls.parse(req.url).pathname;
	if(typeof _rotas[url] === 'function'){
		return _rotas[url](res);
	}else{
		return _defaultError(res);
	}
}

var _raiz = function(res)
{		
	arquivo.abrirHtml('index.html', res);	
}

var _teste = function(res)
{
	arquivo.abrirHtml('teste.html', res);
}

var _defaultError = function(res)
{
	arquivo.abrirHtml('error.html', res);
}

var _rotas = {};
_rotas['/'] = _raiz;
_rotas['/teste'] = _teste;


module.exports = {
	router: _router,
	rotas: _rotas
}